import { createAsyncThunk, createEntityAdapter } from "@reduxjs/toolkit";
import { Product } from "../../app/models/product";
import agent from "../../app/api/agent";
import { createSlice } from '@reduxjs/toolkit'
import { RootState } from "../../app/store/configureStore";

const productsAdapter=createEntityAdapter<Product>();


export const fetchProductsAsync=createAsyncThunk<Product[]>(

    'catalog/fetchProductsAsync',
    async()=>{
      try {
        return await agent.Catalog.list();
      } catch (error) {
        console.log(error);
      }
  
    }
  )


  export const fetchProductAsync=createAsyncThunk<Product,string>(

    'catalog/fetchProductAsync',
    async(productId)=>{
      try {
        return await agent.Catalog.details(productId);
      } catch (error) {
        console.log(error);
      }
  
    }
  )
  
  
  
  const catalogSlice = createSlice({
    name: 'catalog',
    initialState:productsAdapter.getInitialState({
        productsLoaded:false,
        status:'idle'
    }), // get initialstate return all collections ids and entities inside coll
    reducers: {},
    extraReducers: (builder=> {
    
        // GetAllListProducts
        builder.addCase(fetchProductsAsync.pending, (state)=>{
          state.status='pendingFetchProductsAsync';
          // console.log(action)
        });
        builder.addCase(fetchProductsAsync.fulfilled,(state,action)=>{
          state.status='idle';
          productsAdapter.setAll(state,action.payload);
          state.productsLoaded=true;

        });
        builder.addCase(fetchProductsAsync.rejected,(state)=>{
          state.status='idle'
        });

        //get single product
        builder.addCase(fetchProductAsync.pending, (state)=>{
            state.status='pendingFetchProductAsync';
            // console.log(action)
          });
          builder.addCase(fetchProductAsync.fulfilled,(state,action)=>{
            state.status='idle';
            productsAdapter.upsertOne(state,action.payload);
  
          });
          builder.addCase(fetchProductAsync.rejected,(state)=>{
            state.status='idle'
          });
    
    
       
    
      })
  });
  

export const productSelectors=productsAdapter.getSelectors((state:RootState)=>state.catalog);

//   export const {} = catalogSlice.actions
  
  export default catalogSlice.reducer
