import Typography from '@mui/material/Typography'
import { useSouqContext } from "../../app/context/SouqContext";
import { TableContainer, Paper, Table, TableHead, TableRow, TableCell, TableBody, IconButton } from '@mui/material';
import { Delete } from '@mui/icons-material';

export default function BasketPage() {

    // const [loading, setLoading] = useState(true);
    // const [basket, setBasket] = useState<Basket|null>(null);
    //  useEffect(() => {

    //   agent.Basket.get().then(basket=>setBasket(basket))
    //   .catch(error=>console.log(error))
    //   .finally(()=>setLoading(false))
    // }, [])
    
    // if(loading)return<Loader message="Loading basket....!!!" />
      const {basket}=useSouqContext();

    if(!basket)return <Typography variant="h3" color="initial"> Basket is empty</Typography>

  return (
    <>
        <Typography sx={{mt:10}}  variant="h4" color="initial"> Buyer Id is :{basket.buyerId}</Typography>
        <TableContainer  sx={{mt:10}}  component={Paper}>
      <Table sx={{ minWidth: 650 }} >
        <TableHead>
          <TableRow>
            <TableCell>Product</TableCell>
            <TableCell align="center">Price</TableCell>
            <TableCell align="center">Quantity</TableCell>
            <TableCell align="center">SubTotal</TableCell>
            <TableCell align="center">Delete</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {basket.items.map((row) => (
            <TableRow
              key={row.productId}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {row.name}
              </TableCell>
              <TableCell align="center">{(row.price/100).toFixed(2)}USD</TableCell>
              <TableCell align="center">{row.quantity}</TableCell>
              <TableCell align="center">{((row.price/100)*row.quantity).toFixed(2)}USD</TableCell>
              <TableCell align="center">
                <IconButton color='error' >
                  <Delete/>

                </IconButton>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>

    </>
  )
}
