import { Box, Pagination, Typography } from "@mui/material";
import { PaginationData } from "../models/PaginationData";


type Props={
    paginationData:PaginationData
    onPageChange:(page:number)=>void
}
export default function AppPagination({paginationData,onPageChange}:Props) {
    const {cuurentPage,pageSize,totalCount,totalPages}=paginationData
  return (
<Box sx={{mt:-1}} display='flex' justifyContent='space-between' alignItems='center'>
                    <Typography sx={{ml:1}} > 
                    Displaying {(cuurentPage - 1) * pageSize + 1} -
                    {cuurentPage * pageSize > totalCount ? totalCount : cuurentPage * pageSize }
                     of {totalCount} items
                    </Typography>
                    <Pagination 
                    shape="rounded"
                    color="primary"
                    size="large"
                    count={totalPages}
                    page={cuurentPage}
                    onChange={(e,page)=>onPageChange(page)}
                    />
                </Box>  )
}
