import Avatar from '@mui/material/Avatar';
import TextField from '@mui/material/TextField';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { Paper } from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';
import { FieldValues, useForm } from 'react-hook-form';
import { LoadingButton } from '@mui/lab';
import { useAppDispatch } from '../../app/store/configureStore';
import { SingInUser } from './accountSlice';
import agent from '../../app/api/agent';

// function Copyright(props: any) {
//   return (
//     <Typography variant="body2" color="text.secondary" align="center" {...props}>
//       {'Copyright © '}
//       <Link color="inherit" href="https://mui.com/">
//         Your Website
//       </Link>{' '}
//       {new Date().getFullYear()}
//       {'.'}
//     </Typography>
//   );
// }

// TODO remove, this demo shouldn't need to reset the theme.
// const defaultTheme = createTheme();

export default function Login() {
    const dispach=useAppDispatch();
    const navigate=useNavigate();

    const {register,handleSubmit,formState:{isSubmitting,isValid,errors}}=useForm({
        mode:'all'
    });

   


  return (
    // <ThemeProvider theme={defaultTheme}>
      <Container component={Paper} maxWidth="sm">
        {/* <CssBaseline /> */}
        <Box
          sx={{
            marginTop: 13,
            p:3,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: 'primary.main' }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
             Register
          </Typography>
          <Box component="form" onSubmit={handleSubmit((data)=>
            agent.Account.register(data))} noValidate sx={{ mt: 1 }}>
            <TextField
              margin="normal"
            //   required
              fullWidth
              label="Username"
            //   autoComplete="email"
              autoFocus
            //   onChange={handleInputChange}
            //   value={values.username}
            {...register('username',{required:true})}
            error={!!errors.username}
            helperText={errors?.username?.message}
            />
             <TextField
              margin="normal"
            //   required
              fullWidth
              label="Email"
            //   autoComplete="email"
            //   onChange={handleInputChange}
            //   value={values.username}
            {...register('email',{required:true})}
            error={!!errors.email}
            helperText={errors?.email?.message}
            />
            <TextField
              margin="normal"
            //   required
              fullWidth
            //   name="password"
              label="Password"
              type="password"
              {...register('password',{required:'كلمة المرور مطلوبة'})}
              error={!!errors.password}
              helperText={errors?.password?.message}



            //   autoComplete="current-password"
            //   onChange={handleInputChange}
            //   value={values.password}
            />
            {/* <FormControlLabel
              control={<Checkbox value="remember" color="primary" />}
              label="Remember me"
            /> */}
            <LoadingButton
            disabled={!isValid}
            loading={isSubmitting}
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              Register
            </LoadingButton>
            <Grid container>
              {/* <Grid item xs>
                <Link href="#" variant="body2">
                  Forgot password?
                </Link>
              </Grid> */}
              <Grid item>
                <Typography 
                component={Link}
                to={"../login"}
               sx={{fontWeight:'bold',color:'primary.main',textDecoration:'none'}}
                >
                  {"Already have an account? Sign In"}
                </Typography>
              </Grid>
            </Grid>
          </Box>
        </Box>
        {/* <Copyright sx={{ mt: 8, mb: 4 }} /> */}
      </Container>
    // { </ThemeProvider> }
  );
}
