import { TableContainer, Paper, Table, TableBody, TableRow, TableCell } from "@mui/material";
import { useSouqContext } from "../../app/context/SouqContext";
import { currencyFormat } from "../../app/helper/helper";

export default function BasketSummary() {
    const {basket}=useSouqContext();
   

    // ??0 if null return 0

    const subtotal = basket?.items.reduce((sum,item)=>sum+(item.quantity*item.price),0)??0;
    const deliveryFee = subtotal>10000000?0:5000;
    return (
        <>
            <TableContainer component={Paper} variant={'outlined'}>
                <Table>
                    <TableBody>
                        <TableRow>
                            <TableCell colSpan={2}>Subtotal</TableCell>
                            <TableCell align="right">{currencyFormat(subtotal)}</TableCell>
                        </TableRow>
                        <TableRow>
                            <TableCell colSpan={2}>Delivery fee*</TableCell>
                            <TableCell align="right">{currencyFormat(deliveryFee)}</TableCell>
                        </TableRow>
                        <TableRow>
                            <TableCell colSpan={2}>Total</TableCell>
                            <TableCell align="right">{currencyFormat(subtotal + deliveryFee)}</TableCell>
                        </TableRow>
                        <TableRow>
                            <TableCell>
                                <span style={{ fontStyle: 'italic' }}>*Orders over 1000 USD qualify for free delivery</span>
                            </TableCell>
                        </TableRow>
                    </TableBody>
                </Table>
            </TableContainer>
           
        </> 
    )
}
