import { Paper, Typography, Stepper, Step, StepLabel, Box, Button, Container } from "@mui/material";
import { useState } from "react";
import AddressForm from "./AddressForm";
import PaymentForm from "./PaymentForm";
import Review from "./Review";
import { FieldValues, FormProvider, useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup"
import { validationSchema } from "./checkoutValidation";
import { useAppDispatch } from "../../app/store/configureStore";
import agent from "../../app/api/agent";
import { clearBasket } from "../basket/basketSlice";
import { LoadingButton } from "@mui/lab";
const steps = ['Shipping address', 'Review your order', 'Payment details'];

function getStepContent(step: number) {
    switch (step) {
        case 0:
            return <AddressForm />;
        case 1: 
            return <Review />;
        case 2:
            return <PaymentForm />;
        default:
            throw new Error('Unknown step');
    }
}

export default function CheckoutPage() {
  ``

    const [activeStep, setActiveStep] = useState(0);

    const [orderNumber, setOrderNumber] = useState(0);
    const [loading, setLoading] = useState(false);
    const dispatch=useAppDispatch();



    const currentValidatoinSchema=validationSchema[activeStep];

    const methods=useForm({

        mode:'all',
        resolver:yupResolver<any>(currentValidatoinSchema)
            });// use form hook

    const handleNext = async (data:FieldValues) => {

        const {nameOnCard,saveAddress,...shippingAddress}=data;
      if(activeStep===steps.length-1){

        setLoading(true);
        try {
            console.log(data);

            const orderNumber =await agent.Orders.create({saveAddress,shippingAddress});
            setOrderNumber(orderNumber);
            setActiveStep(activeStep+1);
            setLoading(false);
            setTimeout(() => {
                dispatch(clearBasket());
            }, 3000);



        } catch (error) {
            console.log(error);

            setLoading(false);

        }
      }else
      {
        setActiveStep(activeStep + 1);


      }
    };

    const handleBack = () => {
        setActiveStep(activeStep - 1);
    };

   

    return (

      
        <FormProvider {...methods} > 
                <Container maxWidth="lg"> 
            <Paper variant="outlined" sx={{ mt: 10, p: { xs: 2, md: 3 } }}>
                <Typography component="h1" variant="h4" align="center">
                    Checkout
                </Typography>
                <Stepper activeStep={activeStep} sx={{ pt: 3, pb: 5 }}>
                    {steps.map((label) => (
                        <Step key={label}>
                            <StepLabel>{label}</StepLabel>
                        </Step>
                    ))}
                </Stepper>
                <>
                    {activeStep === steps.length ? (
                        <>
                            <Typography variant="h5" gutterBottom>
                                Thank you for your order.
                            </Typography>
                            <Typography variant="subtitle1">
                                Your order number is #{orderNumber}. We have emailed your order
                                confirmation, and will send you an update when your order has
                                shipped.
                            </Typography>
                        </>
                    ) : (
                        <form onSubmit={methods.handleSubmit(handleNext)}>
                            {getStepContent(activeStep)}
                            <Box sx={{ display: 'flex', justifyContent: 'flex-end' }}>
                                {activeStep !== 0 && (
                                    <Button onClick={handleBack} sx={{ mt: 3, ml: 1 }}>
                                        Back
                                    </Button>
                                )}
                                <LoadingButton
                                loading={loading}
                                    disabled={!methods.formState.isValid}

                                    variant="contained"
                                   type="submit"
                                    sx={{ mt: 3, ml: 1 }}
                                >
                                    {activeStep === steps.length - 1 ? 'Place order' : 'Next'}
                                </LoadingButton>
                            </Box>
                        </form>
                    )}
                </>
            </Paper> 
        </Container>
        </FormProvider>


    );
}
