import { createAsyncThunk, createSlice, isAnyOf } from '@reduxjs/toolkit'
import { User } from '../../app/models/user';
import { FieldValues } from 'react-hook-form';
import agent from '../../app/api/agent';

interface AccountState{
    user:User | null



}

const initialState :AccountState= {
    user:null

}

export const SingInUser=createAsyncThunk<User,FieldValues>(
    'account/SingInUser',
    async(data,thunkAPI)=>{
        try {
            const user = await agent.Account.login(data);
            localStorage.setItem('user',JSON.stringify(user))
            return user;
        } catch (error:any) {
            return thunkAPI.rejectWithValue({error:error.data})

        
        }


    }


)

export const fetchCurrentUser=createAsyncThunk<User>(
    'account/fetchCurrentUser',
    async(_,thunkAPI)=>{
        try {
            const user = await agent.Account.currentUser();
            localStorage.setItem('user',JSON.stringify(user))
            return user;
        } catch (error:any) {
            return thunkAPI.rejectWithValue({error:error.data})

        
        }


    }


)




const accountSlice = createSlice({
  name: 'account',
  initialState,
  reducers: {},
  extraReducers:(builder =>{

    builder.addMatcher(isAnyOf(SingInUser.fulfilled,fetchCurrentUser.fulfilled),
    (state,action)=>{

        state.user=action.payload;
    });
    builder.addMatcher(isAnyOf(SingInUser.rejected,fetchCurrentUser.rejected),
    (state,action)=>{

        console.log(action.payload) ;
    })

  })
  
});

export const {} = accountSlice.actions

export default accountSlice.reducer
