import Typography from '@mui/material/Typography'
import { useSouqContext } from "../../app/context/SouqContext";
import { TableContainer, Paper, Table, TableHead, TableRow, TableCell, TableBody, Box, Grid } from '@mui/material';
import { Add, Delete, Remove } from '@mui/icons-material';
import { useState } from 'react';
import agent from '../../app/api/agent';
import { LoadingButton } from '@mui/lab';
import BasketSummary from './BasketSummary';
import { currencyFormat } from '../../app/helper/helper';

export default function BasketPage() {

    // const [loading, setLoading] = useState(true);
    // const [basket, setBasket] = useState<Basket|null>(null);
    //  useEffect(() => {

    //   agent.Basket.get().then(basket=>setBasket(basket))
    //   .catch(error=>console.log(error))
    //   .finally(()=>setLoading(false))
    // }, [])
    
    // if(loading)return<Loader message="Loading basket....!!!" />
      const {basket,setBasket,removeItem}=useSouqContext();


      //NEW TYPES FOR VARIABLES IMPORTANT ***ALI_23_04_15
      const [status, setStatus] = useState({
        loading:false,
        name:''

      });
      
      const handleAddItem=(productId:number,name:string)=>{
        setStatus({loading:true,name:name});
        agent.Basket.addItem(productId).then(basket=>setBasket(basket))
        .catch(error=>console.log(error))
        .finally(()=>setStatus({loading:false,name:''}));


    }
    const handleRemoveItem=(productId:number,quantity=1,name:string)=>{
      setStatus({loading:true,name:name});
      agent.Basket.deleteItem(productId,quantity)
      .then(()=>removeItem(productId,quantity))
      .catch(error=>console.log(error))
      .finally(()=>setStatus({loading:false,name:''}));
    }

    if(!basket || basket.items.length===0)return <Typography sx={{mt:10}} variant="h3" color="initial"> Basket is empty</Typography>

  return (
    <>
        <Typography sx={{mt:10}}  variant="h4" color="initial"> Buyer Id is :{basket.buyerId}</Typography>
        <TableContainer  sx={{mt:10}}  component={Paper}>
      <Table sx={{ minWidth: 650 }} >
        <TableHead>
          <TableRow>
            <TableCell>Product</TableCell>
            <TableCell align="center">Price</TableCell>
            <TableCell align="center">Quantity</TableCell>
            <TableCell align="center">SubTotal</TableCell>
            <TableCell align="center">Delete</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {basket.items.map((row) => (
            <TableRow
              key={row.productId}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                <Box display={'flex'} alignItems={'center'}>
                  <img src={row.pictureUrl} alt={row.name} style={{height:50,marginRight:20}} />
                  <span>{row.name}</span>

                </Box>
              </TableCell>
              <TableCell align="center">{currencyFormat(row.price)}</TableCell>
           
                
              <TableCell align="center">
                  
              <LoadingButton 
                loading={status.loading&&status.name==='rem'+row.productId}
                onClick={()=>handleRemoveItem(row.productId,1,'rem'+row.productId)}
                color='error'
                sx={{ fontWeight: 'bolder' }} 
                size="small">
                  <Remove/>
                  </LoadingButton>
                
                {row.quantity}
                <LoadingButton 
                loading={status.loading&&status.name==='add'+row.productId}
                onClick={()=>handleAddItem(row.productId,'add'+row.productId)}
                color='secondary'
                sx={{ fontWeight: 'bolder' }} 
                size="small">
                  <Add/>
                  </LoadingButton>
              
              
              </TableCell>
              
                  
              <TableCell align="center">{((row.price/1000)*row.quantity).toFixed(2)}USD</TableCell>
              <TableCell align="center">
              <LoadingButton 
                 loading={status.loading&&status.name==='del'+row.productId}
                 onClick={()=>handleRemoveItem(row.productId,row.quantity,'del'+row.productId)}
                  color='error'>
                  <Delete/>
                  </LoadingButton>

                
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
      <Grid container spacing={0}>
                  <Grid item xs={6}  />
                  <Grid item xs={6}>
                      <BasketSummary/>
                  
                  </Grid>
                
      </Grid>

    </>
  )
}
