import { useEffect } from "react"
import Loader from "../../app/layout/Loader"
import ProductList from "./ProductList"
import { useAppDispatch, useAppSelector } from "../../app/store/configureStore"
import { fetchProductsAsync, fetchfiltersAsync, productSelectors } from "./catalogSlice"
import { Box, Checkbox, FormControl, FormControlLabel, FormGroup, Grid, Pagination, Paper, Radio, RadioGroup, TextField, Typography } from "@mui/material"

const sortOption =[
   { value:'name', label:'Alphabetical'},
   { value:'lowprice', label:'Price- Low To High'},
   { value:'highprice', label:'Price- High To Low'},


]
export default function Catalog() {
    // const [products, setProducts] = useState<Product[]>([])// context
    const products=useAppSelector(productSelectors.selectAll);
    const dispatch=useAppDispatch();
    const {status,productsLoaded,filtersLoaded,brands,types}=useAppSelector(state=>state.catalog);





    useEffect(() => {

        // agent.Catalog.list()
        //     .then(products => setProducts(products))
        //     .catch(error => console.log(error))
        //     .finally(() => setLoading(false))

        if (!productsLoaded) {
            dispatch(fetchProductsAsync())
            
        }


    }
        , [productsLoaded,dispatch])

        useEffect(() => {

           
            if (!filtersLoaded) {
                dispatch(fetchfiltersAsync())
                
            }
    
    
        }
            , [filtersLoaded,dispatch])
    if (status.includes('pending')) return <Loader message='Loading Products...' />
    return (
        <Grid  container spacing={2}>
            <Grid sx={{mt:3}} item xs={6} sm={2.2} >
                <Paper sx={{mb:2}}>
                    <TextField variant="outlined"
                     label='Search Product'
                     fullWidth />



                </Paper>
                <Paper sx={{mb:2,p:2}}>
                <FormControl>
                    {/* <FormLabel>Gender</FormLabel> */}
                    <RadioGroup
                     >
                            {
                                sortOption.map(({value,label})=>(
                                    <FormControlLabel key={value} value={value} label={label} control={<Radio/>} />

                                ))

                            }
                       
                    </RadioGroup>
                </FormControl>

                </Paper> 
                <Paper sx={{mb:2,p:2}}>
                <FormGroup>
                   
                            {
                               
                                brands.map(brand=>(
                                    <FormControlLabel key={brand} label={brand} control={<Checkbox/>} />

                                ))

                            }
                       
                   
                </FormGroup>

                </Paper>

                <Paper sx={{mb:2,p:2}}>
                        <FormGroup>
                {
                                types.map(type=>(
                                    <FormControlLabel key={type} label={type} control={<Checkbox/>} />

                                ))

                                }
                        </FormGroup>
             

                </Paper>

            </Grid>

            <Grid sx={{mt:3}} item xs={6} sm={9.8} >
            <ProductList products={products}></ProductList>
            </Grid>
            <Grid item xs={6} sm={2.2}/>
            <Grid item xs={6} sm={9.8}>
                <Box sx={{mt:-1}} display='flex' justifyContent='space-between' alignItems='center'>
                    <Typography sx={{ml:1}} > Displaying 1-8 of 60 items</Typography>
                    <Pagination 
                    shape="rounded"
                    color="primary"
                    size="large"
                    count={2}
                    page={1}
                    />
                </Box>
            </Grid>

        </Grid>
    )
}
