import { useEffect } from "react"
import Loader from "../../app/layout/Loader"
import ProductList from "./ProductList"
import { useAppDispatch, useAppSelector } from "../../app/store/configureStore"
import { fetchProductsAsync, fetchfiltersAsync, productSelectors, setProductParams } from "./catalogSlice"
import { Box, Grid, Pagination, Paper, Typography } from "@mui/material"
import ProductSearch from "./ProductSearch"
import RadioButtonGroup from "../../app/components/RadioButtonGroup"
import CheckBoxButton from "../../app/components/CheckBoxButton"

const sortOption =[
   { value:'name', label:'Alphabetical'},
   { value:'lowprice', label:'Price- Low To High'},
   { value:'highprice', label:'Price- High To Low'},


]
export default function Catalog() {
    // const [products, setProducts] = useState<Product[]>([])// context
    const products=useAppSelector(productSelectors.selectAll);
    const dispatch=useAppDispatch();
    const {status,productsLoaded,filtersLoaded,brands,types,productParams,paginationData}=useAppSelector(state=>state.catalog);





    useEffect(() => {

        // agent.Catalog.list()
        //     .then(products => setProducts(products))
        //     .catch(error => console.log(error))
        //     .finally(() => setLoading(false))

        if (!productsLoaded) {
            dispatch(fetchProductsAsync())
            
        }


    }
        , [productsLoaded,dispatch])

        useEffect(() => {

           
            if (!filtersLoaded) {
                dispatch(fetchfiltersAsync())
                
            }
    
    
        }
            , [filtersLoaded,dispatch])
    if (status.includes('pending')) return <Loader message='Loading Products...' />
    return (
        <Grid  container spacing={2}>
            <Grid sx={{mt:3}} item xs={6} sm={2.2} >
                <Paper sx={{mb:2}}>
                  <ProductSearch/>


                </Paper>
                <Paper sx={{mb:2,p:2}}>
               <RadioButtonGroup 
               selectedValue={productParams.orderBy}
               options={sortOption}
               onChange={(e)=>dispatch(setProductParams({orderBy:e.target.value}))}
               />

                </Paper> 
                <Paper sx={{mb:2,p:2}}>

                    <CheckBoxButton 
                    items={types}
                    checked={productParams.types}
                    onChange={(items:string[])=>dispatch(setProductParams({types:items}))}
                    />
              

                </Paper>

                <Paper sx={{mb:2,p:2}}>
                        {/* <FormGroup>
                {
                                types.map(type=>(
                                    <FormControlLabel key={type} label={type} control={<Checkbox/>} />

                                ))

                                }
                        </FormGroup> */}
                        <CheckBoxButton 
                    items={brands}
                    checked={productParams.brands}
                    onChange={(items:string[])=>dispatch(setProductParams({brands:items}))}
                    />
             

                </Paper>

            </Grid>

            <Grid sx={{mt:3}} item xs={6} sm={9.8} >
            <ProductList products={products}></ProductList>
            </Grid>
            <Grid item xs={6} sm={2.2}/>
            <Grid item xs={6} sm={9.8}>
                <Box sx={{mt:-1}} display='flex' justifyContent='space-between' alignItems='center'>
                    <Typography sx={{ml:1}} > Displaying 1-8 of 60 items</Typography>
                    <Pagination 
                    shape="rounded"
                    color="primary"
                    size="large"
                    count={2}
                    page={1}
                    />
                </Box>
            </Grid>

        </Grid>
    )
}
